import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CreateEvent from '../components/CreateEvent';
import Events from '../components/Events';

const UpcomingEvents = () => {
  const [events, setEvents] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false); // State to track user role

  useEffect(() => {
    fetchEvents();
    // Check user role when component mounts
    checkUserRole();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const checkUserRole = async () => {
    try {
      // Make a request to check user role
      const response = await axios.get('/api/user/role');
      setIsAdmin(response.data.role === 'admin');
    } catch (error) {
      console.error('Error checking user role:', error);
    }
  };

  return (
    <div>
      {isAdmin && <CreateEvent setEvents={setEvents} />} {/* Render CreateEvent only if user is admin */}
      <Events events={events} />
    </div>
  );
};

export default UpcomingEvents;
