import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import AuthPage from './pages/AuthPage';
import Sidebar from './components/Sidebar'; // Import the Sidebar component
import Gallery from './components/Gallery'; // Import the Gallery component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSidebarItemClick = (item) => {
    console.log(`Clicked on ${item}`);
  };

  return (
    <Router>
      <div>
        <Navbar setIsLoggedIn={setIsLoggedIn} />
        {isLoggedIn && <Sidebar onSidebarItemClick={handleSidebarItemClick} />} {/* Render Sidebar only if logged in */}
        <Routes>
          <Route path="/signup/login" element={<AuthPage setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/gallery" element={isLoggedIn ? <Gallery /> : <AuthPage setIsLoggedIn={setIsLoggedIn} />} />
          {/* If isLoggedIn is true, render Gallery, otherwise render AuthPage */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
