import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

const Events = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
  });
  const [errors, setErrors] = useState({ email: '', phoneNumber: '' });
  const [createEventFormData, setCreateEventFormData] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
    sport: '',
  });

  useEffect(() => {
    fetchEvents();
    fetchUserRole();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const fetchUserRole = async () => {
    try {
      const response = await axios.get('/api/user');
      setIsAdmin(response.data.role === 'admin');
    } catch (error) {
      console.error('Error fetching user role:', error);
    }
  };

  const openGoogleMaps = (location) => {
    window.open(
      `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(location)}`,
      '_blank'
    );
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleRegistrationSubmit = async (eventId) => {
    if (!validateForm()) {
      return;
    }
    try {
      await axios.post(`/api/events/${eventId}/register`, formData);
      setFormData({ name: '', email: '', phoneNumber: '' });
      fetchEvents();
    } catch (error) {
      console.error('Error submitting registration:', error);
    }
  };

  const validateForm = () => {
    let valid = true;
    const newErrors = {};
    if (!formData.email.includes('@')) {
      newErrors.email = 'Email must contain @';
      valid = false;
    }
    if (!/^\d{10}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Phone number must be 10 digits';
      valid = false;
    }
    setErrors(newErrors);
    return valid;
  };

  const handleCreateEventFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/events', createEventFormData);
      setEvents((prevEvents) => [...prevEvents, response.data]);
      setCreateEventFormData({
        name: '',
        date: '',
        time: '',
        location: '',
        sport: '',
      });
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleCreateEventInputChange = (e) => {
    const { name, value } = e.target;
    setCreateEventFormData({ ...createEventFormData, [name]: value });
  };

  return (
    <div className="events-container">
      {isAdmin && (
        <div className="create-event-container">
          <h2>Create Event</h2>
          <form onSubmit={handleCreateEventFormSubmit}>
            <input
              type="text"
              placeholder="Event Name"
              name="name"
              value={createEventFormData.name}
              onChange={handleCreateEventInputChange}
              required
            />
            <input
              type="date"
              placeholder="Date"
              name="date"
              value={createEventFormData.date}
              onChange={handleCreateEventInputChange}
              required
            />
            <input
              type="time"
              placeholder="Time"
              name="time"
              value={createEventFormData.time}
              onChange={handleCreateEventInputChange}
              required
            />
            <input
              type="text"
              placeholder="Location"
              name="location"
              value={createEventFormData.location}
              onChange={handleCreateEventInputChange}
              required
            />
            <input
              type="text"
              placeholder="Sport"
              name="sport"
              value={createEventFormData.sport}
              onChange={handleCreateEventInputChange}
              required
            />
            <button type="submit">Create Event</button>
          </form>
        </div>
      )}

      <h2>Upcoming Events</h2>
      <div className="events-grid">
        {events.map((event) => (
          <div key={event.id} className="event-card">
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Time: {event.time}</p>
            <p>
              <FontAwesomeIcon
                icon={faMapMarkerAlt}
                onClick={() => openGoogleMaps(event.location)}
              />{' '}
              : {event.location}
            </p>
            <form
              className="registration-form"
              onSubmit={(e) => {
                e.preventDefault();
                handleRegistrationSubmit(event.id);
              }}
            >
              <input
                type="text"
                placeholder="Name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <input
                type="email"
                placeholder="Email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              {errors.email && <span className="error">{errors.email}</span>}
              <input
                type="tel"
                placeholder="Phone Number"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
              />
              {errors.phoneNumber && (
                <span className="error">{errors.phoneNumber}</span>
              )}
              <button type="submit">Register</button>
            </form>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
