// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Notificationbar from './components/Notificationbar';
import Sidebar from './components/Sidebar'; // Import Sidebar component
import Events from './components/Events';
import Footer from './components/Footer';
import AboutUs from './components/AboutUs';
import HomePage from './pages/HomePage';
import AuthPage from './pages/AuthPage';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Maintain authentication status

  // Handler for Sidebar item click
  const handleSidebarItemClick = (item) => {
    // Handle sidebar item click action here
    console.log(`Clicked on ${item}`);
  };

  return (
    <Router>
      <div>
        {isLoggedIn ? (
          <>
            <Notificationbar />
            <Sidebar onSidebarItemClick={handleSidebarItemClick} />
          </>
        ) : (
          <Navbar setIsLoggedIn={setIsLoggedIn} />
        )}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutUs />} />
          <Route path="/events" element={<Events />} />
          <Route path="/support" element={<Footer />} />
          <Route path="/signup/login" element={<AuthPage setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
