  const handleJoinClick = () => {
    // Logic to set username and room
    // For example:
    setUsername(usernameInput);
    setRoom(selectedRoom);
  };

  return (
    <div>
      {/* Your join chat room form */}
      <div className="chat-form-card">
        {/* Your form content */}
      </div>

      {/* Conditionally render the ChatRoom component if username and room are set */}
      {username && room && (
        <ChatRoom username={username} room={room} />
      )}
    </div>
  );
};

