import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell } from '@fortawesome/free-solid-svg-icons';
import '../styles/Notificationbar.css';
import io from 'socket.io-client';

const Notificationbar = ({ socket }) => {
  const [notificationCount, setNotificationCount] = useState(0);
  const [showNotificationBox, setShowNotificationBox] = useState(false);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    if (!socket) return;

    socket.on('newEventCreated', (data) => {
      setNotifications((prevNotifications) => [...prevNotifications, data]);
      setNotificationCount((prevCount) => prevCount + 1);
    });

    return () => {
      socket.off('newEventCreated');
    };
  }, [socket]);

  const handleNotificationClick = () => {
    setShowNotificationBox(!showNotificationBox);
    if (showNotificationBox) {
      setNotificationCount(0); // Reset notification count when notification box is opened

      // Emit event to indicate that the user has seen the notifications
      socket.emit('notificationsSeen');
    }
  };

  return (
    <div className="navbar">
      <div className="icons">
        <div className="icon" style={{ marginRight: '10px' }} onClick={handleNotificationClick}>
          <FontAwesomeIcon icon={faBell} className="iconimg" />
          <div className="counter">{notificationCount}</div>
        </div>
      </div>
      {showNotificationBox && (
        <div className="notification-box">
          {/* Render notifications */}
          {notifications.map((notification, index) => (
            <div key={index} className="notification-item">
              {notification.message}
            </div>
          ))}
          {/* You can add further UI elements for acknowledgements or actions */}
        </div>
      )}
    </div>
  );
};

export default Notificationbar;
