import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faComments, faUser, faUsers, faPaperPlane } from '@fortawesome/free-solid-svg-icons';
import io from 'socket.io-client';
import '../styles/ChatRoom.css';

const ChatRoom = ({ username, room }) => {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const newSocket = io("http://localhost:3020");
    setSocket(newSocket);

    newSocket.emit('joinRoom', { username, room });

    return () => {
      newSocket.disconnect();
    };
  }, [username, room]);

  useEffect(() => {
    if (!socket) return;

    socket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    socket.on('roomUsers', ({ users }) => {
      setUsers(users);
    });

    // Handle leave room event
    socket.on('userLeft', (leftUsername) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { username: 'Server', text: `${leftUsername} has left the room`, time: new Date().toLocaleTimeString() },
      ]);
      setUsers((prevUsers) => prevUsers.filter(user => user.username !== leftUsername));
    });
  }, [socket]);

  const sendMessage = (msg) => {
    if (socket) {
      const messageData = {
        username: username,
        text: msg
      };
      socket.emit('chatMessage', messageData);
    }
  };

  const leaveChatRoom = () => {
    const leaveRoom = window.confirm('Are you sure you want to leave the chatroom?');
    if (leaveRoom) {
      window.location = '../index.html';
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const msg = e.target.elements.msg.value.trim();
    if (msg) {
      sendMessage(msg);
      e.target.elements.msg.value = '';
    }
  };

  return (
    <div className="chatroom">
      <div className="chatroom-header">
        <h1 className="title"><i className="fas fa-smile"></i> ChatRoom</h1>
        <button onClick={leaveChatRoom} className="leave-btn">Leave Room</button>
      </div>
      <div className="chatroom-main">
        <div className="chatroom-sidebar">
          <div className="sidebar-header">
            <FontAwesomeIcon icon={faUser} className="icon" />
            <span className="username">Current User:</span>
          </div>
          <div className="current-user">
            <span className="username">{username}</span>
          </div>
          <div className="sidebar-header">
            <FontAwesomeIcon icon={faUsers} className="icon" />
            <span className="users-header">users</span>
          </div>
          <ul className="room-users">
            {users.map((user, index) => (
              <li key={index}>
                <FontAwesomeIcon icon={faUser} className="icon" />
                <span className="username">{user.username}</span>
              </li>
            ))}
          </ul>
        </div>
        <div className="chatroom-messages">
          {messages.map((message, index) => (
            <div key={index} className="message">
              <p className="meta">
                <span className="username">
                  {message.username === username ? 'You' : message.username}
                </span>
                <span className="time">{message.time}</span>
              </p>
              <p className="text">{message.text}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="chatroom-form-container">
        <form id="chat-form" onSubmit={handleSubmit}>
          <input id="msg" type="text" placeholder="Enter Message" autoComplete="off" name="msg" required />
          <button type="submit" className="send-btn">
            <FontAwesomeIcon icon={faPaperPlane} className="icon" />
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatRoom;




































