import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faEnvelope, faCog, faHome } from '@fortawesome/free-solid-svg-icons';
import '../styles/Notificationbar.css';

const Notificationbar = () => {
  const [notificationCount, setNotificationCount] = useState(3);
  const [messageCount, setMessageCount] = useState(5);
  const [showNotifications, setShowNotifications] = useState(false);
  const [showMessages, setShowMessages] = useState(false);

  const handleNotificationClick = () => {
    setShowNotifications(!showNotifications);
    // Reset notification count when clicked
    setNotificationCount(0);
  };

  const handleMessageClick = () => {
    setShowMessages(!showMessages);
    // Reset message count when clicked
    setMessageCount(0);
  };

  return (
    <div className="navbar">
      <span className="logo">Sports</span>
      <div className="icons">
        <div className="icon">
          <a href="/home"> {/* Link to Home */}
            <FontAwesomeIcon icon={faHome} className="iconimg" />
          </a>
        </div>
        <div className="icon" onClick={handleNotificationClick}>
          <FontAwesomeIcon icon={faBell} className="iconimg" />
          <div className="counter">{notificationCount}</div>
        </div>
        {showNotifications && (
          <div className="notification-box">
            {/* Display notifications here */}
            <p>Notification 1</p>
            <p>Notification 2</p>
            <p>Notification 3</p>
          </div>
        )}
        <div className="icon" onClick={handleMessageClick}>
          <FontAwesomeIcon icon={faEnvelope} className="iconimg" />
          <div className="counter">{messageCount}</div>
        </div>
        {showMessages && (
          <div className="message-box">
            {/* Display messages here */}
            <p>Message 1</p>
            <p>Message 2</p>
            <p>Message 3</p>
          </div>
        )}
        <div className="icon">
          <FontAwesomeIcon icon={faCog} className="iconimg" />
          {/* Settings icon */}
        </div>
      </div>
    </div>
  );
};

export default Notificationbar;
