// SignUp.js

import React, { useState } from 'react';
import '../styles/SignUp.css'; 

const SignUp = ({ setIsLogin }) => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [agreeTerms, setAgreeTerms] = useState(false);

  const handleSignup = async () => {
    if (!fullName || !email || !password || !confirmPassword || !agreeTerms) {
      alert('Please fill in all fields and agree to terms');
      return;
    }
    if (!email.includes('@')) {
      alert('Invalid email');
      return;
    }
    if (password.length < 8) {
      alert('Password must be at least 8 characters long');
      return;
    }
    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    try {
      const response = await fetch('/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fullName, email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        // Signup successful
        localStorage.setItem('token', data.token);
        setIsLogin(true);
      } else {
        // Signup failed
        alert(data.message || 'Signup failed');
      }
    } catch (error) {
      console.error('Error during signup:', error);
      alert('Signup failed');
    }
  };

  return (
    <div className="sign-up-container">
      <h2>Sign Up</h2>
      <label htmlFor="fullName">Full Name:</label>
      <input
        type="text"
        id="fullName"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)}
      />
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <label htmlFor="confirmPassword">Confirm Password:</label>
      <input
        type="password"
        id="confirmPassword"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
      />
      <div className="terms-checkbox">
        <input
          type="checkbox"
          id="agreeTerms"
          checked={agreeTerms}
          onChange={() => setAgreeTerms(!agreeTerms)}
        />
        <p><label htmlFor="agreeTerms">By signing up, you agree to our Terms and Privacy Policy</label></p>
      </div>
      <button onClick={handleSignup}>Sign Up</button>
      <p>Already have an account? <span onClick={() => setIsLogin(true)}>Login</span></p>
    </div>
  );
};

export default SignUp;
