import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CreateEvent from '../components/CreateEvent';
import Events from '../components/Events';

const UpcomingEvents = () => {
  const [events, setEvents] = useState([]);
  const [userRole, setUserRole] = useState(null); // State to store user role

  useEffect(() => {
    fetchEvents();
    fetchUserRole(); // Fetch user role when component mounts
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const fetchUserRole = async () => {
    try {
      const response = await axios.get('/api/user/role'); // Adjust this endpoint based on your server setup
      setUserRole(response.data.role);
    } catch (error) {
      console.error('Error fetching user role:', error);
    }
  };

  return (
    <div>
      {userRole === 'admin' && <CreateEvent setEvents={setEvents} />} {/* Render CreateEvent only if user is admin */}
      <Events events={events} />
    </div>
  );
};

export default UpcomingEvents;
