import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Gallery from './components/Gallery';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const navigateToSignup = () => {
    // Implement navigation to the signup page
  };

  const navigateToForgotPassword = () => {
    // Implement navigation to the forgot password page
  };

  return (
    <Router>
      <Routes>
        <Route path="/login">
          <Login setIsLoggedIn={setIsLoggedIn} navigateToSignup={navigateToSignup} navigateToForgotPassword={navigateToForgotPassword} />
        </Route>
        <Route path="/gallery">
          {isLoggedIn ? <Gallery /> : <Navigate to="/login" />}
        </Route>
        <Navigate from="*" to="/login" />
      </Routes>
    </Router>
  );
};

export default App;
