import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Notificationbar from './components/Notificationbar'; // Import Notificationbar component
import Sidebar from './components/Sidebar'; 
import Footer from './components/Footer';
import AboutUs from './components/AboutUs';
import HomePage from './pages/HomePage';
import AuthPage from './pages/AuthPage';
import UserProfile from './components/UserProfile';
import ForumPage from './pages/ForumPage';
import Gallery from './components/Gallery'; 
import Logout from './components/Logout';
import UserList from './components/UserList';
import Events from './components/Events';
import io from 'socket.io-client';

function App() {
  const [socket, setSocket] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); 

  useEffect(() => {
    const newSocket = io('http://localhost:3009'); // Replace with your server URL
    setSocket(newSocket);

    return () => newSocket.close();
  }, []);

  const handleSidebarItemClick = (item) => {
    console.log(`Clicked on ${item}`);
  };

  return (
    <Router>
      <div>
        {/* Render Notificationbar component */}
        <Notificationbar socket={socket} /> 

        {/* Render Sidebar or Navbar based on isLoggedIn state */}
        {isLoggedIn ? (
          <>
            <Sidebar onSidebarItemClick={handleSidebarItemClick} />
          </>
        ) : (
          <Navbar setIsLoggedIn={setIsLoggedIn} />
        )}

        {/* Define routes */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutUs />} />
          <Route path="/events" element={<Events socket={socket} />} />
          <Route path="/support" element={<Footer />} />
          <Route path="/signup/login" element={<AuthPage setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/userprofile" element={<UserProfile />} />
          <Route path="/forums" element={<ForumPage />} />
          <Route path="/logout" element={<Logout />} />
          <Route
            path="/users"
            element={<UserList />}
          />
          <Route
            path="/gallery"
            element={isLoggedIn ? <Gallery /> : <Navigate to="/signup/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
