// Notificationbar.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faEnvelope, faCog, faHome, faSearch } from '@fortawesome/free-solid-svg-icons';
import '../styles/Notificationbar.css';

const Notificationbar = () => {
  const [notifications, setNotifications] = useState([]);
  const [searchVisible, setSearchVisible] = useState(false);

  const handleRead = () => {
    setNotifications([]);
  };

  const notificationCount = 3;

  return (
    <div className="navbar">
      <span className="logo">Sports</span>
      <div className="icons">
        <div className="icon">
          <Link to="/home"> {/* Link to Home */}
            <FontAwesomeIcon icon={faHome} className="iconimg" />
          </Link>
        </div>
        <div className="gap"></div> {/* Create a gap */}
        <div className="icon">
          <Link to="/search"> {/* Link to Search */}
            <FontAwesomeIcon icon={faSearch} className="iconimg" />
          </Link>
        </div>
        <div className="icon">
          <Link to="/notifications"> {/* Link to Notifications */}
            <FontAwesomeIcon icon={faBell} className="iconimg" />
            <div className="counter">{notificationCount}</div>
          </Link>
        </div>
        <div className="icon">
          <Link to="/messages"> {/* Link to Messages */}
            <FontAwesomeIcon icon={faEnvelope} className="iconimg" />
            <div className="counter">{notificationCount}</div>
          </Link>
        </div>
        <div className="icon">
          <Link to="/settings"> {/* Link to Settings */}
            <FontAwesomeIcon icon={faCog} className="iconimg" />
            <div className="counter">{notificationCount}</div>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Notificationbar;
