// App.js
import React, { useState, useEffect } from 'react';
import Gallery from './components/Gallery';
import { getCurrentUser } from './auth'; // Function to retrieve current user from your authentication system

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const user = getCurrentUser(); // Retrieve user information from your authentication system
    setCurrentUser(user); // Set the current user state
  }, []);

  return (
    <div>
      <h1>Your App</h1>
      <Gallery user={currentUser} /> {/* Pass user information to the Gallery component */}
    </div>
  );
};

export default App;
