import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faComment, faPaperPlane, faCamera, faDownload } from '@fortawesome/free-solid-svg-icons';
import '../styles/Gallery.css';

const Gallery = ({ user, profilePicture}) => {
  const [loggedIn, setLoggedIn] = useState(true);
  const [posts, setPosts] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState('');
  const [isPosting, setIsPosting] = useState(false);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = () => {
    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = () => {
        const url = reader.result;
        setIsPosting(true);
        // Automatically scroll to the new post after uploading
        setTimeout(() => {
          window.scrollTo(0, document.body.scrollHeight);
        }, 100);
        setPosts(prevPosts => [
          {
            id: prevPosts.length + 1,
            type: selectedFile.type.startsWith('image') ? 'image' : 'video',
            url: url,
            caption: caption,
            likes: 0,
            comments: [],
            username: user.name,
            userProfilePic: profilePicture, // Use profilePicture prop here
            createdAt: new Date().toLocaleString()
          },
          ...prevPosts
        ]);
        setSelectedFile(null);
        setCaption('');
        setIsPosting(false);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleLike = (postId) => {
    setPosts(prevPosts =>
      prevPosts.map(post =>
        post.id === postId && !post.liked ?
        { ...post, likes: post.likes + 1, liked: true, username: user.name, userProfilePic: profilePicture } : post
      )
    );
  };

  const handleCommentChange = (postId, value) => {
    setPosts(prevPosts =>
      prevPosts.map(post =>
        post.id === postId ? { ...post, commentInput: value } : post
      )
    );
  };

  const handleComment = (postId) => {
    const post = posts.find(p => p.id === postId);
    if (post.commentInput.trim() !== '') {
      setPosts(prevPosts =>
        prevPosts.map(p =>
          p.id === postId ? { ...p, comments: [...p.comments, 
            { text: p.commentInput, user: user.name, 
              userProfilePic: profilePicture }], 
              commentInput: '', showComments: true } : p
        )
      );
    }
  };

  const handleDownload = (url) => {
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'image_or_video');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="gallery">
      <div className="header">
        <h2>Kilifi Sport Hub Gallery</h2>
        {loggedIn && (
          <div className="upload-container">
            <label htmlFor="file-upload" className="file-upload-label">
              <FontAwesomeIcon icon={faCamera} /> Upload Photo/Video
              <input id="file-upload" type="file" accept="image/*,video/*" onChange={handleFileChange} style={{ display: 'none' }} />
            </label>
            {selectedFile && (
              <div>
                {selectedFile.type.startsWith('image') && (
                  <img src={URL.createObjectURL(selectedFile)} alt="Selected File" className="selected-file-preview" />
                )}
                <input type="text" value={caption} onChange={(e) => setCaption(e.target.value)} placeholder="Write a caption..." />
                <button onClick={handleUpload}>{isPosting ? 'Posting...' : 'Post'}</button>
              </div>
            )}
          </div>
        )}
      </div>
      
      <div className="gallery-container">
        {posts.map(post => (
          <div className="post" key={post.id}>
            <div className="user-info">
              <div className='profile-info'>
                <img src={post.userProfilePic} alt="Profile" className="profile-pic" />
                <span className="full-name">{post.username}</span>
              </div>
              <div className='date-info'>
                <span className="post-date">{post.createdAt}</span>
              </div>
            </div>
            {post.caption && <h3>{post.caption}</h3>}
            <div className="post-meta">
              <span className="post-type">{post.type === 'image' ? 'Photo' : 'Video'}</span>
            </div>
            {post.type === 'image' && <img src={post.url} alt="" />}
            {post.type === 'video' && <video controls><source src={post.url} type="video/mp4" /></video>}
            
            {loggedIn && (
              <div className="comment-input">
                <img src={profilePicture} alt="Profile" className="profile-pic" /> {/* Use profilePicture prop */}
                <input
                  type="text"
                  value={post.commentInput}
                  onChange={(e) => handleCommentChange(post.id, e.target.value)}
                  placeholder="Write a comment here..."
                />
                <button onClick={() => handleComment(post.id)}>
                  <FontAwesomeIcon icon={faPaperPlane} />
                </button>
              </div>
            )}

            <div className="reactions">
              <button disabled={!loggedIn || post.liked} onClick={() => handleLike(post.id)}>
                <FontAwesomeIcon icon={faThumbsUp} /> {post.likes}
              </button>
              <button onClick={() => setPosts(prevPosts => prevPosts.map(p => p.id === post.id ? { ...p, showComments: !p.showComments } : p))}>
                <FontAwesomeIcon icon={faComment} /> {post.comments.length}
              </button>
              <button onClick={() => handleDownload(post.url)}>
                <FontAwesomeIcon icon={faDownload} /> Download
              </button>
            </div>
            
            {post.showComments && (
              <div className="comments-container">
                <div className="comments" >
                  {post.comments.map((comment, index) => (
                    <div key={index} className="comment">
                      <img src={comment.userProfilePic} alt="Profile" className="profile-pic" />
                      <p><strong>{comment.username}:</strong> {comment.text}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
