import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const ChatRoom = ({ username, room }) => {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const newSocket = io();
    setSocket(newSocket);

    newSocket.emit('joinRoom', { username, room });

    return () => {
      newSocket.disconnect();
    };
  }, [username, room]);

  useEffect(() => {
    if (!socket) return;

    socket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    socket.on('roomUsers', ({ users }) => {
      setUsers(users);
    });

    // Handle leave room event
    socket.on('userLeft', (leftUsername) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { username: 'Server', text: `${leftUsername} has left the room`, time: new Date().toLocaleTimeString() },
      ]);
      setUsers((prevUsers) => prevUsers.filter(user => user.username !== leftUsername));
    });
  }, [socket]);
  

  const sendMessage = (msg) => {
    if (socket) {
      socket.emit('chatMessage', msg);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const msg = e.target.elements.msg.value.trim();
    if (msg) {
      sendMessage(msg);
      e.target.elements.msg.value = '';
    }
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <h1><i className="fas fa-smile"></i> ChatCord</h1>
        <a id="leave-btn" className="btn" onClick={() => window.location.reload()}>Leave Room</a>
      </header>
      <main className="chat-main">
        <div className="chat-sidebar">
          <h3><i className="fas fa-comments"></i> Room Name:</h3>
          <h2>{room}</h2>
          <h3><i className="fas fa-users"></i> Current User:</h3>
          <p>{username}</p> {/* Display the current user */}
          <h3><i className="fas fa-users"></i> Users</h3>

          <ul>
            {users.map((user, index) => (
              <li key={index}>{user}</li>
            ))}
          </ul>

        </div>
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className="message">
              <p className="meta">{message.username === username ? 'You' : message.username} <span>{message.time}</span></p>
              <p className="text">{message.text}</p>
            </div>
          ))}
        </div>
      </main>
      <div className="chat-form-container">
        <form id="chat-form" onSubmit={handleSubmit}>
          <input
            id="msg"
            type="text"
            placeholder="Enter Message"
            autoComplete="off"
            name="msg"
            required
          />
          <button type="submit" className="btn"><i className="fas fa-paper-plane"></i> Send</button>
        </form>
      </div>
    </div>
  );
};

export default ChatRoom;
