import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import '../styles/Events.css';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [socket, setSocket] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
    sport: ''
  });

  useEffect(() => {
    const newSocket = io('http://localhost:3020');
    setSocket(newSocket);

    newSocket.on('newEvent', (newEvent) => {
      setEvents(prevEvents => [...prevEvents, newEvent]);
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/events', formData);
      const newEvent = response.data;
      setEvents(prevEvents => [...prevEvents, newEvent]);
      socket.emit('newEvent', newEvent); // Emit event to other clients
      setFormData({
        name: '',
        date: '',
        time: '',
        location: '',
        sport: ''
      });
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="events-container">
      <h2>Upcoming Events</h2>
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="name" placeholder="Event Name" value={formData.name} onChange={handleInputChange} required />
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
        <input type="text" name="time" placeholder="Event Time" value={formData.time} onChange={handleInputChange} required />
        <input type="text" name="location" placeholder="Event Location" value={formData.location} onChange={handleInputChange} required />
        <input type="text" name="sport" placeholder="Event Sport" value={formData.sport} onChange={handleInputChange} required />
        <button type="submit">Create Event</button>
      </form>
      <div className="events-grid">
        {events.map(event => (
          <div key={event.id} className="event-card">
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Time: {event.time}</p>
            <p>Location: {event.location}</p>
            {/* Other event details */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
