import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faComments, faUser, faUsers } from '@fortawesome/free-solid-svg-icons'; // Import Font Awesome icons
import '../styles/ChatRoom.css';

const ChatRoom = ({ username, room }) => {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const newSocket = io("http://localhost:3020");
    setSocket(newSocket);

    newSocket.emit('joinRoom', { username, room });

    return () => {
      newSocket.disconnect();
    };
  }, [username, room]);

  useEffect(() => {
    if (!socket) return;

    socket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    socket.on('roomUsers', ({ users }) => {
      setUsers(users);
    });

    // Handle leave room event
    socket.on('userLeft', (leftUsername) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { username: 'Server', text: `${leftUsername} has left the room`, time: new Date().toLocaleTimeString() },
      ]);
      setUsers((prevUsers) => prevUsers.filter(user => user.username !== leftUsername));
    });
  }, [socket]);

  const sendMessage = (msg) => {
    if (socket) {
      const messageData = {
        username: username, 
        text: msg
      };
      socket.emit('chatMessage', messageData);
    }
  };

  const leaveChatRoom = () => {
    const leaveRoom = window.confirm('Are you sure you want to leave the chatroom?');
    if (leaveRoom) {
      window.location = '../index.html';
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const msg = e.target.elements.msg.value.trim();
    if (msg) {
      sendMessage(msg);
      e.target.elements.msg.value = '';
    }
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <h1><i className="fas fa-smile"></i> ChatRoom</h1>
        <button onClick={leaveChatRoom} className="btn">Leave Room</button>
      </header>
      <main className="chat-main">
        <div className="chat-sidebar">
          <h3><FontAwesomeIcon icon={faComments} /> Room Name:</h3>
          <h2>{room}</h2>
          <h3><FontAwesomeIcon icon={faUser} /> Current User:</h3>
          <p>{username}</p>
          <h3><FontAwesomeIcon icon={faUsers} /> users</h3>
          <ul>
            {users.map((user, index) => (
              <li key={index}>
                <FontAwesomeIcon icon={faUser} />
                {user.username}
              </li>
            ))}
          </ul>
        </div>
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div key={index} className="message">
              <p className="meta">{message.username === username ? 'You' : message.username} <span>{message.time}</span></p>
              <p className="text">{message.text}</p>
            </div>
          ))}
        </div>
      </main>
      <div className="chat-form-container">
        <form id="chat-form" onSubmit={handleSubmit}>
          <input
            id="msg"
            type="text"
            placeholder="Enter Message"
            autoComplete="off"
            name="msg"
            required
          />
          <button type="submit" className="btn"><i className="fas fa-paper-plane"></i> Send</button>
        </form>
      </div>
    </div>
  );
};

export default ChatRoom;
