import React, { useState } from 'react';
import '../styles/Login.css'; 

const Login = ({ setIsLogin, navigateToSignup, navigateToForgotPassword }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }), // Ensure email and password are sent in the request body
      });

      if (response.ok) {
        const data = await response.json();
        // Login successful
        localStorage.setItem('token', data.token);
        setIsLogin(true); // Set login state to true
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Login failed');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <p>Forgot password? <span onClick={navigateToForgotPassword}>Click here to reset</span></p>
      <p>Don't have an account? <span onClick={navigateToSignup}>Sign up</span></p>
    </div>
  );
};

export default Login;
