import React, { useState } from 'react';
import SignUp from '../components/SignUp';
import Login from '../components/Login';
import ForgotPassword from '../components/ForgotPassword';
import NewPassword from '../components/NewPassword';

const AuthPage = () => {
  const [step, setStep] = useState('signup')

  const navigateToSignup = () => {
    setStep('signup');
  };

  const navigateToLogin = () => {
    setStep('login');
  };

  const navigateToForgotPassword = () => {
    setStep('forgotPassword');
  };

  const navigateToNewPassword = () => {
    setStep('newPassword');
  };

  // Define setIsLogin here or receive it as a prop from a parent component

  return (
    <div>
      {step === 'signup' && <SignUp setIsLogin={navigateToLogin} />}
      {step === 'login' && <Login setIsLogin={navigateToLogin} navigateToSignup={navigateToSignup}
       navigateToForgotPassword={navigateToForgotPassword} />}

      {step === 'forgotPassword' && <ForgotPassword navigateToNewPassword={navigateToNewPassword} />}
      {step === 'newPassword' && <NewPassword />}
    </div>
  );
};

export default AuthPage;
