import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import NotificationBar from './components/NotificationBar'; // Import NotificationBar component
import Events from './components/Events';
import Footer from './components/Footer';
import AboutUs from './components/AboutUs';
import HomePage from './pages/HomePage';
import AuthPage from './pages/AuthPage'; // Import AuthPage component
import Sports from './components/Sports';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Maintain authentication status

  return (
    <Router>
      <div>
        {isLoggedIn ? ( // Conditionally render Navbar or NotificationBar based on isLoggedIn
          <NotificationBar />
        ) : (
          <Navbar setIsLoggedIn={setIsLoggedIn} />
        )}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/about" element={<AboutUs />} />
          <Route path="/events" element={<Events />} />
          <Route path="/sports" element={<Sports />} />
          <Route path="/support" element={<Footer />} />
          <Route path="/signup/login" element={<AuthPage setIsLoggedIn={setIsLoggedIn} />} /> {/* Include AuthPage in the route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
