import React, { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

const ENDPOINT = 'http://localhost:3001'; // Update with your server URL

const Chat = ({ userId, username }) => {
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [recipientId, setRecipientId] = useState('');
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = socketIOClient(ENDPOINT);
    setSocket(newSocket);

    newSocket.emit('login', userId);

    newSocket.on('receive-message', ({ senderId, message }) => {
      console.log('Message received:', senderId, message);
      setMessages(prevMessages => [...prevMessages, { senderId, message }]);
    });

    return () => newSocket.disconnect();
  }, [userId]);

  const handleMessageInputChange = (e) => {
    setMessageInput(e.target.value);
  };

  const handleRecipientChange = (e) => {
    setRecipientId(e.target.value);
  };

  const sendMessage = () => {
    if (socket && recipientId && messageInput.trim() !== '') {
      socket.emit('send-message', { senderId: userId, recipientId, message: messageInput });
      setMessages(prevMessages => [...prevMessages, { senderId: userId, message: messageInput }]);
      setMessageInput('');
    }
  };

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            {msg.senderId === userId ? 'You: ' : `${msg.senderId}: `}
            {msg.message}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={recipientId}
        onChange={handleRecipientChange}
        placeholder="Recipient ID"
      />
      <input
        type="text"
        value={messageInput}
        onChange={handleMessageInputChange}
        placeholder="Type your message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
