import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Events.css';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
    sport: ''
  });
  const [registrationVisible, setRegistrationVisible] = useState({});
  const [filters, setFilters] = useState({
    sport: '',
    date: '',
    location: ''
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filters, events]);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/events', formData);
      setEvents(prevEvents => [...prevEvents, response.data]);
      setFormData({
        name: '',
        date: '',
        time: '',
        location: '',
        sport: ''
      });
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegistrationToggle = (eventId) => {
    setRegistrationVisible({ ...registrationVisible, [eventId]: !registrationVisible[eventId] });
  };

  const handleRegistrationSubmit = (eventId, formData) => {
    // Handle registration submission logic, e.g., send data to backend
    console.log(`Event ID: ${eventId}, Registration Data:`, formData);
    // Reset registration form visibility after submission
    setRegistrationVisible({ ...registrationVisible, [eventId]: false });
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const applyFilters = () => {
    let filtered = events;
    if (filters.sport) {
      filtered = filtered.filter(event => event.sport.toLowerCase().includes(filters.sport.toLowerCase()));
    }
    if (filters.date) {
      filtered = filtered.filter(event => event.date === filters.date);
    }
    if (filters.location) {
      filtered = filtered.filter(event => event.location.toLowerCase().includes(filters.location.toLowerCase()));
    }
    setFilteredEvents(filtered);
  };

  return (
    <div className="events-container">
      <h2>Upcoming Events</h2>
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="name" placeholder="Event Name" value={formData.name} onChange={handleInputChange} required />
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
        <input type="time" name="time" placeholder="Event Time" value={formData.time} onChange={handleInputChange} required />
        <input type="text" name="location" placeholder="Event Location" value={formData.location} onChange={handleInputChange} required />
        <input type="text" name="sport" placeholder="Event Sport" value={formData.sport} onChange={handleInputChange} required />
        <button type="submit">Create Event</button>
      </form>
      <div className="filters">
        <select value={filters.sport} onChange={handleFilterChange} name="sport">
          <option value="">All Sports</option>
          <option value="Football">Football</option>
          <option value="Basketball">Basketball</option>
          <option value="Tennis">Tennis</option>
          <option value="Swimming">Swimming</option>
          <option value="Running">Running</option>
          {/* Add more options as needed */}
        </select>
        <input type="date" name="date" value={filters.date} onChange={handleFilterChange} />
        <input type="text" name="location" placeholder="Filter by Location" value={filters.location} onChange={handleFilterChange} />
      </div>
      <div className="events-grid">
        {filteredEvents.map(event => (
          <div key={event.id} className="event-card">
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Time: {event.time}</p>
            <p>Location: {event.location}</p>
            <button onClick={() => handleRegistrationToggle(event.id)}>Register</button>
            {registrationVisible[event.id] && <RegistrationForm eventId={event.id} onSubmit={handleRegistrationSubmit} />}
          </div>
        ))}
      </div>
    </div>
  );
};

const RegistrationForm = ({ eventId, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
  });
  const [errors, setErrors] = useState({
    email: '',
    phoneNumber: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) {
      // If form validation fails, do not submit
      return;
    }
    onSubmit(eventId, formData);
    // Clear form fields after submission
    setFormData({
      name: '',
      email: '',
      phoneNumber: '',
    });
  };

  const validateForm = () => {
    let valid = true;
    const newErrors = {};
    // Validate email
    if (!formData.email.includes('@')) {
      newErrors.email = 'Email must contain @';
      valid = false;
    }
    // Validate phone number
    if (!/^\d{10}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Phone number must be 10 digits';
      valid = false;
    }
    setErrors(newErrors);
    return valid;
  };

  return (
    <form className="registration-form" onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" name="name" value={formData.name} onChange={handleChange} required />
      <input type="email" placeholder="Email" name="email" value={formData.email} onChange={handleChange} required />
      {errors.email && <span className="error">{errors.email}</span>}
      <input type="tel" placeholder="Phone Number" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} required />
      {errors.phoneNumber && <span className="error">{errors.phoneNumber}</span>}
      <button type="submit">Submit</button>
    </form>
  );
};

export default Events;
