import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faEnvelope, faCog, faUser } from '@fortawesome/free-solid-svg-icons';
import io from 'socket.io-client'; // Import socket.io-client library
import '../styles/Notificationbar.css';

const Notificationbar = () => {
  const [notificationCount, setNotificationCount] = useState(0);
  const [messageCount, setMessageCount] = useState(0);
  const [showNotificationBox, setShowNotificationBox] = useState(false);
  const [showMessageBox, setShowMessageBox] = useState(false);

  useEffect(() => {
    const socket = io('http://localhost:3020'); // Connect to the Socket.IO server

    // Event listener for receiving notifications or messages
    socket.on('notification', (data) => {
      console.log('Received notification:', data);
      // Handle incoming notifications or messages
      // Update notification count or show notifications in real-time
    });

    return () => {
      socket.disconnect(); // Disconnect from the Socket.IO server
    };
  }, []);

  const handleNotificationClick = () => {
    setShowNotificationBox(!showNotificationBox);
    setNotificationCount(0);
  };

  const handleMessageClick = () => {
    setShowMessageBox(!showMessageBox);
    setMessageCount(0);
  };

  const handleSettingsClick = () => {
    // Handle settings click
  };

  return (
    <div className="navbar">
      <span className="user">
        <FontAwesomeIcon icon={faUser} className="icon" />
      </span>
      <div className="icons">
        <div className="icon" style={{ marginRight: '10px' }} onClick={handleNotificationClick}>
          <FontAwesomeIcon icon={faBell} className="iconimg" />
          <div className="counter">{notificationCount}</div>
        </div>
        <div className="icon" style={{ marginRight: '10px' }} onClick={handleMessageClick}>
          <FontAwesomeIcon icon={faEnvelope} className="iconimg" />
          <div className="counter">{messageCount}</div>
        </div>
        <div className="icon">
          <div className="icon" style={{ marginRight: '80px' }} onClick={handleSettingsClick}>
            <FontAwesomeIcon icon={faCog} className="iconimg" />
          </div>
        </div>
      </div>
      {showNotificationBox && (
        <div className="notification-box">
          {/* Notification Box Content */}
        </div>
      )}
      {showMessageBox && (
        <div className="message-box">
          {/* Message Box Content */}
        </div>
      )}
    </div>
  );
};

export default Notificationbar;
