import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import '../styles/Events.css';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [filter, setFilter] = useState({
    sport: '',
    date: '',
    location: '',
  });
  const [registrationVisible, setRegistrationVisible] = useState({});
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
    sport: ''
  });
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = io('http://localhost:3020');
    setSocket(newSocket);

    // Fetch events data from backend or API
    fetchEvents();

    newSocket.on('notification', (data) => {
      // Handle event registration notifications
      console.log('Received notification:', data);
      // Example: Display notification to the user
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
      setFilteredEvents(response.data); // Ensure filtered events are also updated
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({
      ...filter,
      [name]: value,
    });
  };

  useEffect(() => {
    // Filter events based on filter options
    let filtered = events;
    if (filter.sport) {
      filtered = filtered.filter(event => event.sport.toLowerCase().includes(filter.sport.toLowerCase()));
    }
    if (filter.date) {
      filtered = filtered.filter(event => event.date === filter.date);
    }
    if (filter.location) {
      filtered = filtered.filter(event => event.location.toLowerCase().includes(filter.location.toLowerCase()));
    }
    setFilteredEvents(filtered);
  }, [filter, events]);

  const handleRegistrationToggle = (eventId) => {
    setRegistrationVisible(prevState => ({
      ...prevState,
      [eventId]: !prevState[eventId]
    }));
  };

  const handleRegistrationSubmit = (eventId, formData) => {
    // Handle registration submission logic, e.g., send data to backend
    console.log(`Event ID: ${eventId}, Registration Data:`, formData);
    // Reset registration form visibility after submission
    setRegistrationVisible(prevState => ({
      ...prevState,
      [eventId]: false
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/events', formData);
      fetchEvents(); // Refresh events after submission
      setFormData({
        name: '',
        date: '',
        time: '',
        location: '',
        sport: ''
      }); // Reset form after submission
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="events-container">
      <h2>Upcoming Events</h2>
      <div className="filters">
        <select value={filter.sport} onChange={handleFilterChange} name="sport">
          <option value="">All Sports</option>
          <option value="Football">Football</option>
          <option value="Basketball">Basketball</option>
          <option value="Tennis">Tennis</option>
          <option value="Swimming">Swimming</option>
          <option value="Running">Running</option>
          {/* Add more options as needed */}
        </select>
        <input type="date" name="date" value={filter.date} onChange={handleFilterChange} />
        <select value={filter.location} onChange={handleFilterChange} name="location">
          <option value="">All Locations</option>
          <option value="Location A">Location A</option>
          <option value="Location B">Location B</option>
          <option value="Location C">Location C</option>
          <option value="Location D">Location D</option>
          <option value="Location E">Location E</option>
          {/* Add more options as needed */}
        </select>
      </div>
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="name" placeholder="Event Name" value={formData.name} onChange={handleInputChange} required />
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} required />
        <input type="text" name="time" placeholder="Event Time" value={formData.time} onChange={handleInputChange} required />
        <input type="text" name="location" placeholder="Event Location" value={formData.location} onChange={handleInputChange} required />
        <input type="text" name="sport" placeholder="Event Sport" value={formData.sport} onChange={handleInputChange} required />
        <button type="submit">Create Event</button>
      </form>
      <div className="events-grid">
        {filteredEvents.map(event => (
          <div key={event.id} className="event-card">
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Time: {event.time}</p>
            <p>Location: {event.location}</p>
            <button onClick={() => handleRegistrationToggle(event.id)}>Register</button>
            {registrationVisible[event.id] && <RegistrationForm eventId={event.id} onSubmit={handleRegistrationSubmit} />}
          </div>
        ))}
      </div>
    </div>
  );
};

const RegistrationForm = ({ eventId, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(eventId, formData);
    // Clear form fields after submission
    setFormData({
      name: '',
      email: '',
    });
  };

  return (
    <form className="registration-form" onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" name="name" value={formData.name} onChange={handleChange} required />
      <input type="email" placeholder="Email" name="email" value={formData.email} onChange={handleChange} required />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Events;
