import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faImage } from '@fortawesome/free-solid-svg-icons';
import '../styles/UserProfile.css'; // Import the CSS file for styling

const UserProfile = () => {
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [profilePic, setProfilePic] = useState('');
  const [isCreatingAccount, setIsCreatingAccount] = useState(false);

  const handleCreateAccount = () => {
    if (username.trim() !== '') {
      const newUser = {
        id: users.length + 1,
        username: username,
        profilePic: profilePic || 'https://via.placeholder.com/150', 
      };
      setUsers([newUser, ...users]); 
      setUsername('');
      setProfilePic('');
      setIsCreatingAccount(false);
    }
  };

  const handleAddUser = () => {
    setIsCreatingAccount(true);
  };

  const totalUsers = users.length;
  const visibleUsers = users.slice(0, 10);
  const remainingUsers = totalUsers - 10;

  return (
    <div className="user-profile-container">
      <h1>User Profile</h1>
      <div className="members-count">Members joined: {totalUsers}</div>
      <div className="user-list">
        <div className="add-user" onClick={handleAddUser}>
          <FontAwesomeIcon icon={faPlus} className="add-icon" />
        </div>
        {visibleUsers.map((user, index) => (
          <div key={user.id} className="user">
            <img src={user.profilePic} alt={user.username} className="profile-pic" />
            <span className="username">{user.username}</span>
          </div>
        ))}
        {remainingUsers > 0 && (
          <div key="additional-user" className="user additional-user">
            +{remainingUsers}
          </div>
        )}
      </div>
      {isCreatingAccount && (
        <div className="create-account">
          <input
            type="text"
            placeholder="Type your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input-field"
          />
          <div className="upload-profile">
            <label htmlFor="profile-pic-input">
              <FontAwesomeIcon icon={faImage} className="upload-icon" />
            </label>
            <input
              type="file"
              id="profile-pic-input"
              accept="image/*"
              onChange={(e) => setProfilePic(URL.createObjectURL(e.target.files[0]))}
              className="file-input"
            />
          </div>
          <button onClick={handleCreateAccount}>Create Account</button>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
