import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCamera } from '@fortawesome/free-solid-svg-icons';
import profileImg from '../components/images/profileImg.jpg'; 
import '../styles/UserProfile.css';


const UserProfile = ({ username }) => {
  const availableSports = ['Soccer', 'Basketball', 'Tennis', 'Golf', 'Swimming'];  
  const [user, setUser] = useState({
    name: username,
    profilePicture: profileImg, 
    favouriteSports: [], 
    userStats: {
      posts: 0,
      reactions: 0,
    },
  });

  const updateFavouriteSports = (newSports) => {
    setUser({ ...user, favouriteSports: newSports });
  };

  const handlePictureUpload = (e) => {
    const reader = new FileReader();
    reader.onload = () => {
      updateProfilePicture(reader.result);
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  const updateProfilePicture = (newPicture) => {
    setUser({ ...user, profilePicture: newPicture });
  };

  const [isInputVisible, setIsInputVisible] = useState(false);
  const [selectedSport, setSelectedSport] = useState('');

  const handleAddSport = () => {
    if (selectedSport) {
      updateFavouriteSports([...user.favouriteSports, selectedSport]);
      setSelectedSport('');
      setIsInputVisible(false);
    }
  };

  return (
  
    <div className="user-profile">
      <div className="profile-picture-container">
        <img src={user.profilePicture} alt={user.name} className="profile-picture" /> 
        <div className="user-profile-update" 
        onClick={() => document.getElementById('upload-picture').click()}>
  <FontAwesomeIcon icon={faCamera} />
  <input
    id="upload-picture"
    type="file"
    accept="image/*"
    style={{ display: 'none' }}
    onChange={handlePictureUpload}
  />
</div>
        <h3 className="username">{user.name}</h3>
      </div>
      


      <h5 className="favourite-sports">Favourite Sports:</h5>
      <div className="sports-list">
        {user.favouriteSports.map((sport, index) => (
          <div key={index} className="sport-item">
            <span>{sport}</span>
            <button onClick={() => {
              const newSports = [...user.favouriteSports];
              newSports.splice(index, 1);
              updateFavouriteSports(newSports);
            }}>Remove</button>
          </div>
        ))}
      </div>
      <button className="add-sport-button" 
      onClick={() => setIsInputVisible(!isInputVisible)}>Add Sport</button>

      {isInputVisible && (
        <div>
          <select value={selectedSport} onChange={(e) => setSelectedSport(e.target.value)}>
            <option value="">Choose a sport...</option>
            {availableSports.map((sport, index) => (
              <option key={index} value={sport}>{sport}</option>
            ))}
          </select>
          <button onClick={handleAddSport}>Add</button>
        </div>
      )}

      <ul className="status-list">
        <li>Posts: {user.userStats.posts}</li>
        <li>Reactions: {user.userStats.reactions}</li>
      </ul>
    </div>
  );
};

export default UserProfile;
