import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faCamera, faDownload } from '@fortawesome/free-solid-svg-icons';
import '../styles/Gallery.css';

const Gallery = () => {
  // State variables
  const [posts, setPosts] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [caption, setCaption] = useState('');
  const [isPosting, setIsPosting] = useState(false);
  const [userRole, setUserRole] = useState('');

  // Fetch posts and set user role on component mount
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('/api/posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();

    const role = localStorage.getItem('userRole');
    setUserRole(role || '');
  }, []);

  // Handle file input change
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };









   //// Handle upload of photo or video
const handleUpload = async () => {
  try {
    if (selectedFile && userRole === 'admin') {
      setIsPosting(true);

      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('caption', caption);
      formData.append('type', selectedFile.type.startsWith('image') ? 'image' : 'video');
      formData.append('likes', 0);

      // Create new post object
      const newPost = {
        id: posts.length + 1,
        type: selectedFile.type.startsWith('image') ? 'image' : 'video',
        url: URL.createObjectURL(selectedFile),
        caption: caption,
        likes: 0,
        createdAt: new Date().toLocaleString(),
      };

      setSelectedFile(null);
      setCaption('');
      setPosts([newPost, ...posts]); // Update posts array with new post

      // Send post data to server
      const response = await axios.post('/api/posts', formData);

      // If post saved successfully, update local state with the returned post data
      if (response.status === 200) {
        // Assuming the server returns the newly created post data
        const responseData = response.data;
        newPost.id = responseData.id; // Update the ID from the server response
        setPosts([newPost, ...posts]); // Update posts array with new post
        setSelectedFile(null);
        setCaption('');
      }

      setIsPosting(false);
    }
  } catch (error) {
    console.error('Error uploading post:', error);
    setIsPosting(false);
  }
};

  // Handle like on post
  const handleLike = async (postId) => {
    try {
      const response = await axios.put(`/api/posts/${postId}/like`);
      setPosts(prevPosts =>
        prevPosts.map(post =>
          post.id === postId && !post.liked
            ? { ...post, likes: post.likes + 1, liked: true }
            : post
        )
      );
    } catch (error) {
      console.error('Error updating like:', error);
    }
  };

  // Handle download of image or video
  const handleDownload = (url) => {
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'image_or_video');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="gallery">
      <div className="header">
        <h2>Kilifi Sport Hub Gallery</h2>
        {userRole === 'admin' && (
          <div className="upload-container">
            {/* File upload input */}
            <label htmlFor="file-upload" className="file-upload-label">
              <FontAwesomeIcon icon={faCamera} /> Upload Photo/Video
              <input id="file-upload" type="file" accept="image/*,video/*" onChange={handleFileChange} style={{ display: 'none' }} />
            </label>
            {/* Display selected file preview and caption input */}
            {selectedFile && (
              <div>
                <img src={URL.createObjectURL(selectedFile)} alt="Selected File" className="selected-file-preview" />
                <input type="text" value={caption} onChange={(e) => setCaption(e.target.value)} placeholder="Write a caption..." />
                <button onClick={handleUpload}>{isPosting ? 'Posting...' : 'Post'}</button>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="gallery-container">
        {/* Display posts */}
        {posts.map(post => (
          <div className="post" key={post.id}>
            {/* User info */}
            <div className="user-info">
              <span className="full-name">Kilifi Sports</span>
              <div className="date-info">
                <span className="post-date">{post.createdAt}</span>
              </div>
            </div>
            {/* Display caption and post type */}
            {post.caption && <h3>{post.caption}</h3>}
            <div className="post-meta">
              <span className="post-type">{post.type === 'image' ? 'Photo' : 'Video'}</span>
            </div>
            {/* Display image or video */}
            {post.type === 'image' && <img src={post.url} alt="" />}
            {post.type === 'video' && <video controls><source src={post.url} type="video/mp4" /></video>}
            
            {/* Like and download buttons */}
            <div className="reactions">
              <button disabled={post.liked} onClick={() => handleLike(post.id)}>
                <FontAwesomeIcon icon={faThumbsUp} /> {post.likes}
              </button>
              <button onClick={() => handleDownload(post.url)}>
                <FontAwesomeIcon icon={faDownload} /> Download
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Gallery;
