import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Events.css';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    time: '',
    location: '',
    sport: ''
  });
  const [registrationVisible, setRegistrationVisible] = useState({});

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('/api/events');
      setEvents(response.data);
      setFilteredEvents(response.data); // Initialize filtered events with all events
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/events', formData);
      setEvents(prevEvents => [...prevEvents, response.data]);
      setFormData({
        name: '',
        date: '',
        time: '',
        location: '',
        sport: ''
      });
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegistrationToggle = (eventId) => {
    setRegistrationVisible({ ...registrationVisible, [eventId]: !registrationVisible[eventId] });
  };

  const handleRegistrationSubmit = (eventId, formData) => {
    // Handle registration submission logic, e.g., send data to backend
    console.log(`Event ID: ${eventId}, Registration Data:`, formData);
    // Reset registration form visibility after submission
    setRegistrationVisible({ ...registrationVisible, [eventId]: false });
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  useEffect(() => {
    // Filter events based on filter options
    let filtered = events;
    if (formData.name) {
      filtered = filtered.filter(event => event.name.toLowerCase().includes(formData.name.toLowerCase()));
    }
    if (formData.date) {
      filtered = filtered.filter(event => event.date === formData.date);
    }
    if (formData.time) {
      filtered = filtered.filter(event => event.time === formData.time);
    }
    if (formData.location) {
      filtered = filtered.filter(event => event.location.toLowerCase().includes(formData.location.toLowerCase()));
    }
    if (formData.sport) {
      filtered = filtered.filter(event => event.sport.toLowerCase().includes(formData.sport.toLowerCase()));
    }
    setFilteredEvents(filtered);
  }, [formData, events]);

  return (
    <div className="events-container">
      <h2>Upcoming Events</h2>
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="name" placeholder="Event Name" value={formData.name} onChange={handleInputChange} />
        <input type="date" name="date" value={formData.date} onChange={handleInputChange} />
        <input type="time" name="time" placeholder="Event Time" value={formData.time} onChange={handleInputChange} />
        <input type="text" name="location" placeholder="Event Location" value={formData.location} onChange={handleInputChange} />
        <input type="text" name="sport" placeholder="Event Sport" value={formData.sport} onChange={handleInputChange} />
        <button type="submit">Create Event</button>
      </form>
      <div className="filters">
        <input type="text" name="name" placeholder="Filter by Name" value={formData.name} onChange={handleFilterChange} />
        <input type="date" name="date" placeholder="Filter by Date" value={formData.date} onChange={handleFilterChange} />
        <input type="time" name="time" placeholder="Filter by Time" value={formData.time} onChange={handleFilterChange} />
        <input type="text" name="location" placeholder="Filter by Location" value={formData.location} onChange={handleFilterChange} />
        <input type="text" name="sport" placeholder="Filter by Sport" value={formData.sport} onChange={handleFilterChange} />
      </div>
      <div className="events-grid">
        {filteredEvents.map(event => (
          <div key={event.id} className="event-card">
            <h3>{event.name}</h3>
            <p>Date: {event.date}</p>
            <p>Time: {event.time}</p>
            <p>Location: {event.location}</p>
            <button onClick={() => handleRegistrationToggle(event.id)}>Register</button>
            {registrationVisible[event.id] && <RegistrationForm eventId={event.id} onSubmit={handleRegistrationSubmit} />}
          </div>
        ))}
      </div>
    </div>
  );
};

const RegistrationForm = ({ eventId, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(eventId, formData);
    // Clear form fields after submission
    setFormData({
      name: '',
      email: '',
    });
  };

  return (
    <form className="registration-form" onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" name="name" value={formData.name} onChange={handleChange} required />
      <input type="email" placeholder="Email" name="email" value={formData.email} onChange={handleChange} required />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Events;
