import React, { useState } from 'react';
import { BrowserRouter as  Navigate } from 'react-router-dom';
import axios from 'axios';

import '../styles/Login.css';

const Login = ({ setIsLoggedIn, navigateToSignup, navigateToForgotPassword }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [redirectToGallery, setRedirectToGallery] = useState(false); // State for redirection

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { email, password });

      if (response.status === 200) {
        const { role } = response.data;
        localStorage.setItem('userRole', role);
        setIsLoggedIn(true);
        setRedirectToGallery(true); // Set redirection to true upon successful login
      } else {
        setError('Incorrect email or password');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('Login failed');
    }
  };

  if (redirectToGallery) {
    return <Navigate to="/gallery" />; // Redirect to gallery if redirectToGallery is true
  }

  return (
    <div className="login-container">
      <h2>Login</h2>
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      {error && <p className="error-message">{error}</p>}
      <p>Forgot password? <span onClick={navigateToForgotPassword}>Click here to reset</span></p>
      <p>Don't have an account? <span onClick={navigateToSignup}>Sign up</span></p>
    </div>
  );
};

export default Login;
