import React, { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

const ENDPOINT = 'http://localhost:3001'; // Update with your backend server URL

function Chat({ user }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [recipientId, setRecipientId] = useState('');
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = socketIOClient(ENDPOINT);
    setSocket(newSocket);

    // Clean up socket connection
    return () => newSocket.disconnect();
  }, []);

  useEffect(() => {
    if (socket) {
      // Listen for receive-message event
      socket.on('receive-message', ({ senderId, text }) => {
        console.log('Message received:', senderId, text);
        setMessages([...messages, { senderId, text }]);
      });
    }
  }, [socket, messages]);

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleRecipientChange = (e) => {
    setRecipientId(e.target.value);
  };

  const handleMessageSubmit = (e) => {
    e.preventDefault();
    if (socket && recipientId && message.trim()) {
      // Send message to recipient
      socket.emit('send-message', { senderId: user.id, recipientId, text: message });
      setMessages([...messages, { senderId: user.id, text: message }]);
      setMessage('');
    }
  };

  return (
    <div className="Chat">
      <h1>Welcome, {user.username}!</h1>
      <div className="message-container">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <strong>{msg.senderId === user.id ? 'You' : msg.senderId}: </strong>
            {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={handleMessageSubmit}>
        <input
          type="text"
          value={recipientId}
          onChange={handleRecipientChange}
          placeholder="Recipient ID"
        />
        <input
          type="text"
          value={message}
          onChange={handleMessageChange}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default Chat;
