import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane, faComments, faUser, faUsers } from '@fortawesome/free-solid-svg-icons';
import '../styles/ChatRoom.css';

const ChatRoom = ({ username, room }) => {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const newSocket = io('http://localhost:3001', {
      autoConnect: false,
    });

    newSocket.on('connect', () => {
      console.log('Connected to server');
    });
    
    console.log("Socket connected:", newSocket);
    setSocket(newSocket);

    newSocket.emit('joinRoom', { username, room });

    return () => {
      newSocket.disconnect();
    };
  }, [username, room]);

  useEffect(() => {
    if (!socket) return;

    socket.on('message', (message) => {
      setMessages(prevMessages => [...prevMessages, message]);
    });

    socket.on('roomUsers', ({ users }) => {
      console.log(users)
      setUsers(users);
    });

    socket.on('userJoined', (joinedUsername) => {
      setMessages(prevMessages => [
        ...prevMessages,
        { username: 'Server', text: `${joinedUsername} has joined the room`, time: new Date().toLocaleTimeString() }
      ]);
    });

    socket.on('userLeft', (leftUsername) => {
      setMessages(prevMessages => [
        ...prevMessages,
        { username: 'Server', text: `${leftUsername} has left the room`, time: new Date().toLocaleTimeString() }
      ]);
      setUsers(prevUsers => prevUsers.filter(user => user !== leftUsername));
    });

    // Add the current user to the users list when connecting to the room
    setUsers(prevUsers => {
      if (!prevUsers.includes(username)) {
        return [...prevUsers, username];
      }
      return prevUsers;
    });

  }, [socket, username]);

  const sendMessage = () => {
    if (messageInput.trim() !== '') {
      const newMessage = {
        username: username,
        text: messageInput,
        time: new Date().toLocaleTimeString()
      };
      // Update the messages state with the new message
      setMessages(prevMessages => [...prevMessages, newMessage]);
      
      // Emit the new message to the server
      socket.emit('chatMessage', newMessage);
      
      // Clear the message input
      setMessageInput('');
    }
  };
  

  const handleSubmit = (e) => {
    e.preventDefault();
    const msg = e.target.elements.msg.value.trim();
    if (msg) {
      sendMessage(msg);
      e.target.elements.msg.value = '';
    }
  };

  const leaveChatRoom = () => {
    const leaveRoom = window.confirm('Are you sure you want to leave the chatroom?');
    if (leaveRoom) {
      window.location = '../index.html'; 
    }
  };

  return (
    <div className="chatroom-container">
      <div className="chatroom">
        <header className="chatroom-header">
          <h1 className="title"><FontAwesomeIcon icon={faComments} /> ChatRoom</h1>
          <button onClick={leaveChatRoom} className="leave-btn">Leave Room</button>
        </header>
        <div className="chatroom-main">
          <div className="chatroom-sidebar">
            <div className="sidebar-header">
              <FontAwesomeIcon className="icon" icon={faComments} />
              <h3>Room Name:</h3>
            </div>
            <h2>{room}</h2>
            <div className="sidebar-header">
              <FontAwesomeIcon className="icon" icon={faUser} />
              <h3>Current User:</h3>
            </div>
            <p>{username}</p>
            <div className="sidebar-header">
              <FontAwesomeIcon className="icon" icon={faUsers} />
              <h3>Users</h3>
            </div>
            <ul className="room-users">
              {users.map((user, index) => (
                <li key={index}>
                  <p className="username">{user}</p>
                </li>
              ))}
            </ul>
          </div>
          <div className="message-thread">
            <div className="chatroom-messages">
              {messages.map((message, index) => (
                <div key={index} className={`message ${message.username === username ? 'you' : ''}`}>
                  <div className="meta">
                    <p className="username">{message.username}</p>
                    <p className="timestamp">{message.time}</p>
                  </div>
                  <p className="text">{message.text}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="chatroom-form-container">
          <form id="chat-form" onSubmit={handleSubmit}>
            <input
              type="text"
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              placeholder="Type your message..."
            />
            <FontAwesomeIcon icon={faPaperPlane} onClick={sendMessage} />
          </form>
        </div>
      </div>
    </div>
  );
};

export default ChatRoom;
